weapon_vscode () {
	echo "Creating VSCode Hack Environment"
	echo "Usage: $0 <foldername>"
	if [ -z "$1" ]
	then
		echo "No folder name given"
		return 1
	fi
	mkdir -p $1/.vscode

    # .vscode/source_metasploit-handler.zsh
	echo 'c291cmNlICRQUk9KRUNUX0ZPTERFUi8udnNjb2RlL2Vudi56c2gKbXNmY29uc29sZSAtciAiJFBST0pFQ1RfRk9MREVSLy52c2NvZGUvbWV0YXNwbG9pdF9oYW5kbGVyLnJjIg==' | base64 -d > $1/.vscode/source_metasploit-handler.zsh

    # .vscode/source_kali-orbstack.zsh
	echo 'ZXhwb3J0IFBST0pFQ1RfRk9MREVSPSR7UFdEfQpzb3VyY2UgLi8udnNjb2RlL2Vudi56c2gKCgpleGVjIHpzaCAtaSAgIyBtdXN0IGJlIHRoZXJlLiBvdGhlcndpc2UsIHRoZSB0ZXJtaW5hbCB3aWxsIGNsb3NlIGltbWVkaWF0ZWx5Lg==' | base64 -d > $1/.vscode/source_kali-orbstack.zsh

    # .vscode/source_web-delivery.zsh
	echo '' | base64 -d > $1/.vscode/source_web-delivery.zsh

    # .vscode/settings.json
	echo '' | base64 -d > $1/.vscode/settings.json

    # .vscode/source_msfconsole.zsh
	echo 'c291cmNlICRQUk9KRUNUX0ZPTERFUi8udnNjb2RlL2Vudi56c2gKbXNmY29uc29sZSAtciAiJFBST0pFQ1RfRk9MREVSLy52c2NvZGUvbXNmY29uc29sZS5yYyI=' | base64 -d > $1/.vscode/source_msfconsole.zsh

    # .vscode/source.zsh
	echo 'c291cmNlICRQUk9KRUNUX0ZPTERFUi8udnNjb2RlL2Vudi56c2gKZXhlYyB6c2ggLWkgICMgbXVzdCBiZSB0aGVyZS4gb3RoZXJ3aXNlLCB0aGUgdGVybWluYWwgd2lsbCBjbG9zZSBpbW1lZGlhdGVseS4K' | base64 -d > $1/.vscode/source.zsh

    # .vscode/metasploit_handler.rc
	echo '' | base64 -d > $1/.vscode/metasploit_handler.rc

    # .vscode/revshell.zsh
	echo 'c291cmNlICR7UFJPSkVDVF9GT0xERVJ9Ly52c2NvZGUvZW52LnpzaAoKIyBob3hhc2hlbGwgPyAKIyBwd25jYXQtY3MgCnJsd3JhcCAtSSAtY0FyIG5jIC1sdnZwICR7TFBPUlR9IAoK' | base64 -d > $1/.vscode/revshell.zsh

    # .vscode/msfconsole.rc
	echo 'c2V0IFByb21wdCBbS25pZ2h0IE9wZXJhdGlvbiBDb25zb2xlXQpzZXQgTWV0ZXJwcmV0ZXJQcm9tcHQgW0tuaWdodCBFbmVteV0KCiMg' | base64 -d > $1/.vscode/msfconsole.rc

    # .vscode/tasks.json
	echo '' | base64 -d > $1/.vscode/tasks.json

    # .vscode/env.zsh
	echo '' | base64 -d > $1/.vscode/env.zsh

	echo "*" > $1/.vscode/.gitignore
    echo "!env.zsh" >> $1/.vscode/.gitignore
    echo "!metasploit_handler.rc" >> $1/.vscode/.gitignore
	echo "!msfconsole.rc" >> $1/.vscode/.gitignore
	echo "!revshell.zsh" >> $1/.vscode/.gitignore

	echo "Generate Success" "Launched VSCode"
	code $1
}

# Create Alias for weapon_vscode
alias createhackenv=weapon_vscode
alias createhack=weapon_vscode

